	int t[100002] = {};
	priority_queue<P> pque, next_pque;	// first : Long   second : No


	for(int i = 1; i <= N; i++)
		cin >> t[i];

	for(int i = 1; i <= N; i++){
		if(t[i-1] < t[i] && t[i] > t[i+1]){
			pque.push(P(t[i],i));

//			cout << ' ' << i << endl;
		}
	}

	int ans = 0;
	while(pque.size()){
		int dl = L - pque.top().first;
		ans += dl;

		while(pque.size()){
			int No = pque.top().second;
			pque.pop();

			t[No] += dl;

//			cout << t[No] << endl;

			if(t[No] < L)
				next_pque.push(P(t[No],No));
			else {
				t[No] = -1;
				if(No > 1 && t[No-1] > t[No-2]){
					next_pque.push(P(t[No-1],No-1));
				}
				if(No < N && t[No+1] > t[No+2]){

					next_pque.push(P(t[No+1],No+1));
				}
			}
		}
//		cout << endl;
		swap(pque,next_pque);
	}


	cout << ans << endl;
}

#include <iostream>
