using namespace std;
#define INF (1<<29)

int data[100002] , memo[100002];
int n,L;
int recurse(int n){
	if(~memo[n]) return memo[n];
	int ans = 0;
	if(data[n+1] > data[n]) ans = max( ans , recurse(n+1) );
	if(data[n-1] > data[n]) ans = max( ans , recurse(n-1) );

	return memo[n] = ans + (L - data[n]);
}
int main(){
	cin >> n >> L;
	for(int i = 1 ; i <= n ; i++) cin >> data[i];
	memset(memo,-1,sizeof(memo));

	int ans = 0;
	for(int i = 1 ; i <= n ; i++){
		ans = max<int>( ans , recurse(i) ); 
	}
	cout << ans << endl;
}

