int main(){
	int n,l,,buf,pa,h=0,sz,ice[100002];
	queue<pair<int, int> > q;
	priority_queue<pair<int, int> > bufq;

	cin >> n >> l;

	//ice = new int[n+2];
	ice[0] = ice[n+1] = 0;
	for(int i = 1; i < n+1; i++){
		cin >> ice[i];
	}

	for(int i = 1; i < n+1; i++){
		if(ice[i]){
			if((ice[i] > ice[i-1]) && (ice[i] > ice[i+1])){
				bufq.push(make_pair(ice[i], i));
			}
		}
	}

	while(1){
		sz = bufq.size();
		for(int i = 0; i < sz; i++){
			q.push(bufq.top());
			bufq.pop();
		}

		pa = l - q.front().first;

		sz = q.size();
		for(int i = 0; i < sz; i++){
			buf = q.front().second;
			q.pop();

			ice[buf] += pa;
			if(ice[buf] >= l){
				ice[buf] = 0;
				if(ice[buf-1] > ice[buf-2]){
					bufq.push(make_pair(ice[buf-1], buf-1));
				}
				if(ice[buf+1] > ice[buf+2]){
					bufq.push(make_pair(ice[buf+1], buf+1));
				}
			}else{
				bufq.push(make_pair(ice[buf], buf));
			}
		}
		h += pa;

		if(bufq.empty()){
			break;
		}
	}

	cout << h << endl;

	return 0;
}

