using namespace std;

int N, L, a[100000], X, timer = 0, pluser = 1000000, longs[30000];

int main()
{
	cin >> N >> L;

	for (int i = 0; i < N; i++)
	{
		cin >> a[i];
	}

	for (int fin = 0; fin != 1;)
	{
		fin = 1;
		pluser = 60000;
		X = 0;
		if (a[0] > a[1])
		{
			pluser = min(pluser, L - a[0]);
			longs[X] = 0;
			X++;
		}
		for (int i = 1; i < N - 1; i++)
		{
			if (a[i] > a[i - 1] && a[i] > a[i + 1])
			{
				pluser = min(pluser, L - a[i]);
				longs[X] = i;
				X++;
			}
		}
		if (a[N - 1] > a[N - 2])
		{
			pluser = min(pluser, L - a[N - 1]);
			longs[X] = N - 1;
			X++;
		}

		timer += pluser;

		for (int i = 0; i < X; i++)
		{
			if (a[longs[i]] == L - pluser)
			{
				a[longs[i]] = 0;
			}
			else if (a[longs[i]] != 0)
			{
				a[longs[i]] += pluser;
			}
		}
		for (int i = 0; i < N; i++)
		{
			if (a[i] != 0)
			{
				fin = 0;
				break;
			}
		}
	}

	cout << timer << endl;


	return 0;
}

