#define MAX_N 100000

using namespace std;

int dp[MAX_N + 2];
int field[MAX_N + 2];

int ans;

int N;
int L;

bool shape(int i){
	if(field[i] > field[i - 1] && field[i] > field[i + 1]){
		return true;
	}
	return false;
}

void search(int now,int cost,int mode){
	dp[now] = max(dp[now],cost + L - field[now]);
	if(mode != 2){
		if(field[now - 1] == 0);
		else if(field[now] > field[now - 1]){
			search(now - 1,cost + (L - field[now]),1);
		}
	}
	if(mode != 1){
		if(field[now + 1] == 0);
		else if(field[now] > field[now + 1]){
			search(now + 1,cost + (L - field[now]),2);
		}
	}

	return ;

}

void solve(){

	for(int i = 1;i <= N;i++){
		if(shape(i)){
			search(i,0,0);
		}
	}

	ans = 0;
	for(int i = 1;i <= dp[i];i++){
		ans = max(ans,dp[i]);
	}

	return ;
}


int main(){

	scanf("%d%d",&N,&L);
	for(int i = 1;i <= N;i++){
		scanf("%d",field + i);
	}

	solve();

	printf("%d\n",ans);

	return 0;
}

