#include <vector>
using namespace std;

template<class T> inline void chmax(T& a, const T& b) { if(b > a) a = b; }

int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);
	int n, l;
	cin >> n >> l;

	vector<int> icicles(n, 0);
	for(int i = 0; i < n; ++i)
		cin >> icicles[i];

	vector<int> degree(n, 0);
	for(int i = 0; i < n - 1; ++i) {
		if(icicles[i] > icicles[i + 1])
			++degree[i + 1];
		else
			++degree[i];
	}

	queue<int> que;
	for(int i = 0; i < n; ++i)
		if(!degree[i])
			que.push(i);

	int ans = 0;
	vector<int> times(n);
	for(int i = 0; i < n; ++i)
		times[i] = l - icicles[i];

	while(!que.empty()) {
		int idx = que.front();
		que.pop();

		chmax(ans, times[idx]);

		if(idx > 0 && icicles[idx] > icicles[idx - 1]) {
			chmax(times[idx - 1], times[idx] + l - icicles[idx - 1]);
			if(!(--degree[idx - 1]))
				que.push(idx - 1);
		}

		if(idx < n - 1 && icicles[idx] > icicles[idx + 1]) {
			chmax(times[idx + 1], times[idx] + l - icicles[idx + 1]);
			if(!(--degree[idx + 1]))
				que.push(idx + 1);
		}
	}

	cout << ans << endl;

	return EXIT_SUCCESS;
}

