int N, L;
int A[100100];
int memo[100100];

int f(int x) {
  if (memo[x] > 0) return memo[x];
  int t = 0;
  if (x > 0   && A[x-1] > A[x]) t = max(t, f(x-1));
  if (x < N-1 && A[x+1] > A[x]) t = max(t, f(x+1));
  return memo[x] = t + L - A[x];
}

int main() {
  cin >> N >> L;
  for (int i=0; i<N; i++) cin >> A[i];
  int ans = 0;
  for (int i=0; i<N; i++) ans = max(ans, f(i));
  cout << ans << "\n";
  return 0;
}

