typedef pair<int, int> P;
int n, a[100010];

bool check(int x){
	if(x == 1)
		return (a[1] && a[1] > a[2]);
	else if(x == n)
		return (a[n] && a[n - 1] < a[n]);
	else if(x > n || x <= 0)
		return false;
	else
		return (a[x] && (a[x - 1] < a[x] && a[x] > a[x + 1]));
}

int main() {
	int l;
	while(cin >> n >> l){
		memset(a, 0, sizeof(a));
		int ans = 0;
		priority_queue<P> que;
		for(int i = 1; i <= n; ++i){
			scanf("%d", &a[i]);
		}
		for(int i = 1; i <= n; ++i){
			if(check(i))
				que.push(P(a[i], i));
		}
		int cnt = 0;
		while(!que.empty()){
			P p = que.top();
			que.pop();
			int c = l - p.first;
			if(c - cnt > 0)
				cnt += (c - cnt);
			else
				c = 0;
			a[p.second] = 0;
			if(check(p.second - 1))
				que.push(P(a[p.second - 1] - cnt, p.second - 1));
			if(check(p.second + 1))
				que.push(P(a[p.second + 1] - cnt, p.second + 1));
		}
		cout << cnt << endl;
	}

	return 0;
}

