static int T[100000],memo[100000];
int N,L;

int tura(int i){
	if(memo[i]>-1)return memo[i];
	int time=0;
	if(i<N-1&&T[i]<T[i+1])time=tura(i+1);
	if(i>0&&T[i]<T[i-1])time=max(time,tura(i-1));
	return memo[i]=L-T[i]+time;
}

int main(){
	memset(memo,-1,sizeof(memo));
	int i,cnt=0,all=0;
	scanf("%d %d",&N,&L);
	for(i=0;i<N;i++)scanf("%d",&T[i]);
	for(i=0;i<N;i++)cnt=max(cnt,tura(i));
	printf("%d",cnt);
return 0;
}

