#include <iostream>
#include <algorithm>
#include <functional>
using namespace std;
const int MAX_N = 100000;
int n, n2, l;
int icicles[MAX_N + 2], dp[MAX_N + 2];
int main(){
	cin >> n >> l;
	n2 = n;
	for (int i = 1; i <= n; i++){
		cin >> icicles[i];
	}

	while (n){
		for (int i = 1; i <= n2; i++){
			if (icicles[i] > icicles[i - 1] && icicles[i] > icicles[i + 1]){
				dp[i] += l - icicles[i];
				icicles[i] = 0;
				n--;
				if (icicles[i - 1])
					dp[i - 1] = max(dp[i - 1], dp[i]);
				if (icicles[i + 1])
					dp[i + 1] = max(dp[i + 1], dp[i]);
			}
		}
	}
	sort(dp, dp + n2 + 1, greater<int>());
	cout << dp[0] << endl;
	return 0;
}

#include <iostream>
#include <queue>
using namespace std;

int main(){
	int n,l,*ice,buf,pg,ov,sz,h=0;
	queue<int> q;

	cin >> n >> l;

	ice = new int[n+2];
	ice[0] = ice[n+1] = 0;
	for(int i = 1; i < n+1; i++){
		cin >> ice[i];
	}

	for(int i = 1; i < n+1; i++){
		if((ice[i] > ice[i-1]) && (ice[i] > ice[i+1])){
				q.push(i);
		}
	}

	while(1){
		buf = q.front();
		pg = (l - ice[buf]) > 0?(l - ice[buf]):0;

		sz = q.size();
		for(int i = 0; i < sz; i++){
			buf = q.front();
			q.pop();

			ice[buf] += pg;
			ov = (ice[buf] - l) >= 0?(ice[buf] - l):-1;

			if(ov != -1){
				ice[buf] = 0;

				if(ice[buf-1] > ice[buf-2]){
					ice[buf-1] += ov;
					q.push(buf-1);
				}
				if(ice[buf+1] > ice[buf+2]){
					ice[buf+1] += ov;
					q.push(buf+1);
				}
			}else{
				q.push(buf);
			}
		}
		h += pg;

		if(q.empty()){
			break;
		}
	}

	cout << h << endl;

	return 0;
}

