int dp[100005];
int ice[100005];int n,t;
int f(int now){
    if(dp[now]>-1){}
    else if(now==0){
        if(ice[now]>ice[now+1]){
            dp[now]=t-ice[now];
        }else{
            dp[now]=f(now+1);
        }
    }else if(now==(n-1)){
        if(ice[now]>ice[now-1]){
            dp[now]=t-ice[now];
        }else{
            dp[now]=f(now-1);
        }
    }else{
        if(ice[now]>ice[now-1]&&ice[now]>ice[now+1]){
            dp[now]=t-ice[now];
