int func(int table[],int index){

	if(table[index] > table[index-1] && index < N-1 && table[index] > table[index+1]){
		return L - table[index];
	}else if(table[index] > table[index-1] && index == N-1){
		return L - table[index];
	}else{
		return func(table,index+1) + L - table[index];
	}
}

int main(){

	int maximum;
	scanf("%d %d",&N,&L);
	int* table = new int[N];
	time = new int[N];

	for(int i = 0; i < N; i++){
		scanf("%d",&table[i]);
	}

	if(table[0] > table[1]){
		time[0] = L - table[0];
	}else{
		time[0] = func(table,1) + L - table[0];
	}

	for(int i = 1; i <= N-2; i++){
		if(table[i] > table[i-1] && table[i] > table[i+1]){
			time[i] = L - table[i];
		}else{
			if(table[i-1] > table[i] && table[i+1] > table[i]){
				time[i] = max(time[i-1],func(table,i+1)) + L - table[i];
			}else if(table[i-1] > table[i]){
				time[i] = time[i-1] + L - table[i];
			}else if(table[i+1] > table[i]){
				time[i] = func(table,i+1) + L - table[i];
			}
		}
	}

	if(table[N-1] > table[N-2]){
		time[N-1] = L - table[N-1];
	}else{
		time[N-1] = time[N-2] + L - table[N-1];
	}

	maximum = 0;
	for(int i = 0; i < N; i++){
		maximum = max(maximum,time[i]);
	}

	printf("%d\n",maximum);

    return 0;
}

