//#define int long long
using namespace std;
typedef long long ll;
typedef pair<int, int> P;

int n, m;
int t[100002];
const P INF(-1,-1);

bool check(int idx) {
	if (idx == 0) return t[0] > t[1];
	if (idx == n-1) return t[n-2] < t[n-1];
	return t[idx-1] < t[idx] && t[idx] > t[idx+1];
}

P getNext(int idx) {
	if (idx < 0 || n <= idx || !check(idx)) return INF;
	return P(t[idx], idx);
}

signed main()
{
	while (cin >> n >> m) {
		rep(i,n) cin >> t[i];

		int now = 0;
		priority_queue<P> q;
		rep(i,n) if (check(i)) q.push(P(t[i], i));

		while (!q.empty()) {
			P p = q.top(); q.pop();

			int plus = m - (p.first + now);
			if (plus > 0) {
				now += plus;
			} else {
				plus = 0;
			}

			t[p.second] = 0;
			P left = getNext(p.second - 1);
			P right = getNext(p.second + 1);

			if (left != INF) {
				left.first -= now;
				q.push(left);
			}

			if (right != INF) {
				right.first -= now;
				q.push(right);
			}
		}

		cout << now << endl;
	}
}

