    memomemo[a]=1;
    if(memo[a]!=0)return memo[a];
    if(a==0){
        if(lis[a]>lis[a+1])memo[a]=L-lis[a];
        else memo[a]=func(a+1)+(L-lis[a]);
        return memo[a];
