int icicles[100005];
int n, l;

int life (int i )
{
	int res = l - icicles[i];

	// i 番目のつららが一番大きい
	if (icicles[i-1] < icicles[i] && icicles[i] > icicles[i+1] ){
		return res;
	}else	// 前のつららが大きく、後ろのつららが小さい
	if (icicles[i-1] > icicles[i] && icicles[i] > icicles[i+1] ){
		return life (i-1) + res;
	}else	// 前のつららが小さく、後ろのつららが大きい
	if (icicles[i-1] < icicles[i] && icicles[i] < icicles[i+1] ){
		return life (i+1) + res;
	}else{	// i 番目のつららが一番小さい
		return max (life (i-1), life (i+1) ) + res;
	} // end if
}

int main()
{
//	cut here before submit 
//	freopen ("testcase.icicles4", "r", stdin );
	while (scanf ("%d %d", &n, &l ) != EOF ){
		memset (icicles, 0, sizeof (icicles ) );
		for (int i = 1; i <= n; i++ ){
			scanf ("%d", &icicles[i] );
		} // end rep

		int res = 0;
		for (int i = 1; i <= n; i++ ){
			int curr = life (i );
			res = max (res, curr );
		} // end for
		printf ("%d\n", res );

	} // end loop

	return 0;
}

