int ice[100000 + 5] = { 0 };
int len;
int mtim=0;
int n;

void right( int , int );

void left( int , int );

int main()
{
	int i;
	int tim=0;

	cin >> n >> len;

	for (i = 1; i <= n; i++)
	{
		cin >> ice[i];
	}
	for (i = 1; i <= n; i++)
	{
		if (ice[i - 1] < ice[i] && ice[i] > ice[i + 1])
		{
			tim = len - ice[i];
			left(tim , i-1);
			right(tim , i+1);
		}
	}
	cout << mtim << endl;
	return 0;
}

void left( int tim , int now)
{
	if (now == 0)
	{
		if (mtim < tim)
		{
			mtim = tim;
		}
		return;
	}

	tim += len - ice[now];

	if (ice[now] > ice[now - 1])
	{
		left( tim, now - 1);
	}
	else if(mtim < tim)
	{
		mtim = tim;
	}
	return;
}

void right(int tim, int now)
{
	if (now == n + 1)
	{
		if (mtim < tim)
		{
			mtim = tim;
		}
		return;
	}

	tim += len - ice[now];

	if (ice[now] > ice[now + 1])
	{
		right(tim, now + 1);
	}
	else if (mtim < tim)
	{
		mtim = tim;
	}
	return;
}

