using namespace std;

int a[100001];

struct Event {
    int time,num;
    Event(int t,int n) : time(t), num(n) {;}
};

bool operator<(const Event &e,const Event &f) {
    return e.time > f.time;
}

int main() {
    int n,l;
    scanf("%d%d", &n, &l);

    for(int i=0; i<n; ++i) scanf("%d", &a[i]);

    if(n == 1) {
        printf("%d\n", l-a[0]);
        return 0;
    }

    priority_queue<Event> q;
    if(a[0] > a[1])
        q.push(Event(l-a[0],0));

    for(int i=1; i<n-1; ++i) {
        if(a[i] > a[i-1] && a[i] > a[i+1])
            q.push(Event(l-a[i],i));
    }
    if(a[n-1] > a[n-2]) {
        q.push(Event(l-a[n-1],n-1));
    }

    int cnt = 0,ans = 0;

    while(!q.empty()) {
        Event e = q.top(); q.pop();
        if(a[e.num] == 0) continue;
        cnt++; a[e.num] = 0;
        if(cnt == n) ans = e.time;
        if(e.num-1 >= 0 && a[e.num-1] != 0) {
            if(e.num-1 == 0 || a[e.num-2] < a[e.num-1])
                q.push(Event(e.time+l-a[e.num-1],e.num-1));
        }
        if(e.num+1 < n && a[e.num+1] != 0) {
            if(e.num+1 == n-1 || a[e.num+2] < a[e.num+1])
                q.push(Event(e.time+l-a[e.num+1],e.num+1));
        }
    }

    printf("%d\n", ans);
}

