int memo[100005];

int dfs(int v) {
  if(~memo[v])return memo[v];
  int ret = 0;

  if(v > 0 && A[v - 1] > A[v]) ret = dfs(v - 1);
  if(v <= N && A[v + 1] > A[v]) ret = max(ret, dfs(v + 1));

  return memo[v] = (ret + L - A[v]);
} 

