		cin >> tura[i+1];
	}
	int cnt = 1;
	while(cnt){
		cnt = 0;
		FOR(i, 1, N+1){
			if(tura[i-1] < tura[i] && tura[i+1] < tura[i]){
				if(tura[i-1])
					plus[i-1] = max(plus[i-1], L-tura[i]+plus[i]);
				if(tura[i+1])
					plus[i+1] = max(plus[i+1], L-tura[i]+plus[i]);
				res[i] += plus[i]+L-tura[i];
				plus[i] = 0;
				tura[i] = 0;
				cnt++;
			}
		}
	}
	ll ans = 0;
	REP(i, N){
		ans = max(ans, res[i]);
	}
	cout << ans << endl;
	return 0;
}

