int main() {
	int n, l; std::cin >> n >> l;
	std::vector<int>length(n); for (auto& l : length) std::cin >> l;
	std::vector<int> indices(n); for (auto i = 0; i < n; ++i) indices[i] = i;
	std::sort(indices.begin(), indices.end(), [&length](const int a, const int b) {return length[a] > length[b]; });
	std::vector<long long int> time(n);
	for (auto i : indices) {
		if (i == 0) {
			if (length[i] < length[i + 1]) {
				time[i] = l - length[i] + time[i + 1];
			}
			else {
				time[i] = l - length[i];
			}
		}
		else if (i == n - 1) {
			if (length[i] < length[i - 1]) {
				time[i] = l - length[i] + time[i - 1];
			}
			else {
				time[i] = l - length[i];
			}
		}
		else {
			if (length[i] < length[i + 1] && length[i] < length[i - 1]) {
				time[i] = l - length[i] + std::max(time[i + 1], time[i - 1]);
			}
			else if (length[i] < length[i + 1]) {
				time[i] = l - length[i] + time[i + 1];
			}
			else if (length[i] < length[i - 1]) {
				time[i] = l - length[i] + time[i - 1];
			}
			else {
				time[i] = l - length[i];
			}
		}
	}
	std::cout << *std::max_element(time.begin(), time.end()) << std::endl;
}

