using namespace std;

int main() {
    int i;
    int n, l, d, done;
    char flg[100000];
    int a[100000];
    int done_pos[100000];
    priority_queue<pair<int, int>, vector<pair<int, int> >, less<pair<int, int> > > next_broken;

    scanf("%d %d", &n, &l);

    scanf("%d", &a[0]);
    flg[0] = 1;

    for (i=0;i<n;i++) {
        done_pos[i] = 0;
    }

    for (i=1;i<n;i++) {
        scanf("%d", &a[i]);
        flg[i] = a[i-1] < a[i];
        if (flg[i-1] && !flg[i]) {
            next_broken.push(make_pair(a[i-1], i-1));
            done_pos[i-1] = 1;
        }
    }

    if (flg[n-1]) {
        next_broken.push(make_pair(a[n-1], n-1));
        done_pos[n-1] = 1;
    }

    d = 0;
    done = 0;
    while (!next_broken.empty() && done < n) {
        pair<int, int> tmp = next_broken.top();
        int len = tmp.first;
        int pos = tmp.second;

        a[pos] = 0;
        len += d;
        d += (l - len);

        done++;

        if ((pos == 1 && a[0] > a[1]) || (pos > 1 && a[pos-1] > a[pos] && !(done_pos[pos-2] && a[pos-2] != 0) && a[pos-2] < a[pos-1])) {
            a[pos-1] -= d;
            next_broken.push(make_pair(a[pos-1], pos-1));
            done_pos[pos-1] = 1;
        }

        if ((pos == n-2 && a[n-2] < a[n-1]) || (pos < n-2 && a[pos] < a[pos+1] && !(done_pos[pos+2] && a[pos+2] != 0) && a[pos+1] > a[pos+2])) {
            a[pos+1] -= d;
            next_broken.push(make_pair(a[pos+1], pos+1));
            done_pos[pos+1] = 1;
        }

        next_broken.pop();
    }

    printf("%d\n", d);
}

