ll L;
struct icicle{
  ll len,id;
  icicle *l,*r;
  int Grow(){
    if(len == 0) return false;
    if( ( l == nullptr || l->len < len) && ( r == nullptr || r->len < len)){
      ++len;
    }
    if(len == L){
      ll a = l->Grow();
      return true + a;
    }
    return false;
  }
  void Cut(){
    if(len == L){
      len = 0;
    }
  }
};

