using namespace std;

const int N = 100000;
const int M = (1<<29);

int n, l;
int num[N], m[N];

int solve(int current, int dir){
  //cout << "current = " << current << " m = " << m[current] << endl;
  if(current < 0 || current >= n) return 0;
  if(m[current] < M) return m[current];

  if((current == 0 || num[current] > num[current - 1]) &&
     (current == n - 1 || num[current] > num[current + 1])){
    m[current] = l - num[current];
  }else if(dir == 0){ // first
    m[current] = max(solve(current - 1, 1), solve(current + 1, 2)) + l - num[current];
  }else if(dir == 1){ // from right
    m[current] = solve(current - 1, 1) + l - num[current];
  }else{ // from left
    m[current] = solve(current + 1, 2) + l - num[current];
  }
  return m[current];
}

int main(){
  while(cin >> n >> l && n|l){
    for(int i = 0; i < n; i++){
      cin >> num[i];
      m[i] = M;
    }

    int ans = 0;

    for(int i = 0; i < n; i++){
      if(m[i] == M) solve(i, 0);
      ans = max(ans, m[i]);
    }

    cout << ans << endl;
  }
  return 0;
}

