		bool check[100010] = {0};
		queue<int> que;
		for(int i = 1; i <= n; ++i){
			scanf("%d", &a[i]);
		}
		for(int i = 1; i <= n; ++i){
			if(a[i - 1] < a[i] && a[i] > a[i + 1]){
				que.push(i);
			}
		}
		while(!que.empty()){
			int p = que.front();
			que.pop();
			check[p] = true;
			int c = l - a[p] + t[p];
			ans = max(ans, c);
			t[p - 1] = max(t[p - 1], c);
			t[p + 1] = max(t[p + 1], c);
			if(n == 2){
				if(p == 2 && !check[1])
					que.push(1);
				else if(p == 1 && !check[2])
					que.push(2);
			} 
			else if(p == 1 && !check[2]){
				if(check[3] || (a[3] < a[2]))
					que.push(2);
			}
			else if(!check[p - 1]){
				if(check[p - 2] || (a[p - 2] < a[p - 1]))
					que.push(p - 1);
			}
			else if(!check[p + 1]){
				if(check[p + 2] || (a[p + 2] < a[p + 1]))
					que.push(p + 1);
			}
		}
