int icicle[105000], n, l, table[105000];

int snap(int x){
  if(table[x] != 0)return table[x];
  int res = 0;
  if(icicle[x - 1] > icicle[x]){
    res = max(res, snap(x - 1));
  }
  if(icicle[x + 1] > icicle[x]){
    res = max(res, snap(x + 1));
  }
  res += l - icicle[x];
  return table[x] = res;
}

int main(){
