    vector<int> lens(MAX_N, 0);
    vector<int> valleys;

    scanf("%d %d", &n, &l);
    for (int i = 1; i <= n; i++) {
        int ai;
        scanf("%d", &ai);
        lens[i] = ai;
    }

    valleys.push_back(1);
    for (int i = 2; i <= n - 1; i++) {
        if (lens[i] < lens[i - 1] && lens[i] < lens[i + 1]) {
            valleys.push_back(i);
        }
    }
    valleys.push_back(n);

    for (auto& pos: valleys) {
        auto left_time = l - lens[pos], right_time = l - lens[pos];
        auto left_peak = pos;
        while (lens[left_peak - 1] > lens[left_peak]) {
            left_time += l - lens[--left_peak];
        }
        auto right_peak = pos;
        while (lens[right_peak + 1] > lens[right_peak]) {
            right_time += l - lens[++right_peak];
        }
        if (left_time > max_time) { max_time = left_time; }
        if (right_time > max_time) { max_time = right_time; }
    }

    printf("%lld\n", max_time);

    return 0;
}

