int solveVal(int minNum){
	int leftNum = minNum , rightNum = minNum;
	for(int i=minNum-1;i>=0;i--){
		if( data[i] < data[i+1] ){
			leftNum = i+1;
			break;
		}else if( i==0 )leftNum = 0;
	}
	for(int i=minNum+1;i<n;i++){
		if( data[i-1] > data[i] ){
