	for(int i=leftNum; i<minNum;i++) leftVal  += l-data[i];
	for(int i=rightNum;i>minNum;i--) rightVal += l-data[i];
	ans += max(leftVal,rightVal);

	ans += l-data[minNum];

	return ans;
}


