main(){
  cin >> n >> l;
  for(int i=0;i<n;i++){
    int in;
    cin >> in;
    data[0].push_back(in);
  }
  int ans = 0;
  int rem = n;
  int now = 0;
  int next = 1;
  while(rem > 0){
    int valm = INF;
    if(data[now][0] < data[now][1]) valm = min(valm, l-data[now][0]);
    for(int i=1;i<n-1;i++){
      if(data[now][i-1] < data[now][i] && data[now][i] > data[now][i+1]){
        valm = min(valm, l-data[now][i]);
      }
    }
    if(data[now][n-2] < data[now][n-1]) valm = min(valm, l-data[now][n-1]);
    int pre = data[now][0];
    if(data[now][0] > data[now][1]){
      data[now][0] += valm;
      if(data[now][0] >= l){
        data[now][0] = 0;
        --rem;
      }
    }
    for(int i=1;i<n-1;i++){
      if(pre >= data[now][i] || data[now][i] <= data[now][i+1]){
        pre = data[now][i];
        continue;
      }
      pre = data[now][i];
      data[now][i] += valm;
      if(data[now][i] >= l){
        data[now][i] = 0;
        --rem;
      }
    }
    if(pre < data[now][n-1]){
      data[now][n-1] += valm;
      if(data[now][n-1] >= l){
        data[now][n-1] = 0;
        --rem;
      }
    }
    data[next].clear();
    for(int i=0;i<n-1;i++){
      if(data[now][i] == 0 && data[now][i+1] == 0) continue;
      data[next].push_back(data[now][i]);
    }
    if(data[now][n-2] != 0 || data[now][n-1] != 0){
      data[next].push_back(data[now][n-1]);
    }
    n = data[next].size();
    next = now;
    now = 1 - next;
    ans += valm;
  }
  cout << ans << endl;
  return 0;
}

