{
	int N, L;
	cin >> N >> L;

	int I[100002];
	I[0] = 0;
	for(int i=1; i<=N; i++)
		cin >> I[i];
	I[N+1] = 0;

	int T[100002];
	T[0] = 0;
	T[N+1] = 0;
	int max = 0;

	while(true){
		bool flg = false;
		for(int i=1; i<=N; i++)
			if(I[i]>I[i-1]&&I[i]>I[i+1]){
				if(T[i-1]>T[i+1])
					T[i] = T[i-1];
				else
					T[i] = T[i+1];
				T[i] += (L-I[i]);
				if(T[i]>max)
					max = T[i];
				I[i] = 0;
				flg = true;
				i++;
			}

		if(!flg)
			break;
	}

	cout << max << endl;

	return 0;
}

