int n,m;
bool check(int idx){
  if(idx == 0) return a[1] < a[0];
  if(idx == n-1) return a[n-2] < a[n-1];
  return a[idx-1] < a[idx] && a[idx+1] < a[idx];
}
signed main(void) {
        cin >> n >> m;
        a = vi(n);
        rep(i,n)cin >> a[i];
        priority_queue<pii> q;
        rep(i,n)if(check(i))q.push(pii(a[i],i));
        int ans = 0;
        while(q.size()){
            int len = q.top().first;
            int ind = q.top().second;
            q.pop();
            int t = m - (len + ans);
            //cout << ind << " " << len << " " << t << " " << ans << endl;
            if(t > 0) ans += t;
            else t = 0;
            a[ind] = 0;

            if(ind > 0 && check(ind-1)){
                int x = a[ind-1];
                x -= ans;
                q.push(pii(x,ind-1));
            }
            if(ind < n-1 && check(ind+1)){
                int x = a[ind+1];
                x -= ans;
                q.push(pii(x,ind+1));
            }
        }
        cout << ans << endl;

}

