int main() {
	long long int n, l;
	cin >> n >> l;
	vector<long long int> d(n);
	for (int i = 0; i < n; i++) {
		cin >> d[i];
	}
	vector<long long int> t(n, INF);
	while (true) {
		bool h = true;
		for (int i = 0; i < n; i++) {
			if (i == 0) {
				if (d[i] > d[i+1]) {
					t[i] = (l-d[i]);
				} else {
					t[i] = (l-d[i])+t[i+1];
				}
			} else if (i == n-1) {
				if (d[i] > d[i-1]) {
					t[i] = (l-d[i]);
				} else {
					t[i] = (l-d[i])+t[i-1];
				}
			} else {
				if (d[i] > d[i+1] && d[i] > d[i-1]) {
					t[i] = (l-d[i]);
				} else if (d[i] > d[i+1]) {
					t[i] = (l-d[i])+t[i-1];
				} else if (d[i] > d[i-1]) {
					t[i] = (l-d[i])+t[i+1];
				} else {
					t[i] = (l-d[i])+max(t[i+1],t[i-1]);
