  scanf("%d%d",&n,&l);

  kaku[0]=kaku[n+1]=true;

  for(int i=1;i<=n;i++)
    scanf("%d",&tu[i]);

  for(int i=1;i<=n;i++)
    if(tu[i-1]<tu[i] && tu[i]>tu[i+1]){
      pq.push(make_pair(tu[i],i));
      kaku[i] = true;
    }

  while(!pq.empty()){
    pair<int,int> p=pq.top();
    pq.pop();

    t=l-p.first;
    tu[p.second]=0;

    if(!kaku[p.second-1])
      if(tu[p.second-2]<tu[p.second-1]){
	pq.push(make_pair(tu[p.second-1]-t,p.second-1));
	kaku[p.second-1]=true;
      }
    if(!kaku[p.second+1])
      if(tu[p.second+2]<tu[p.second+1]){
	pq.push(make_pair(tu[p.second+1]-t,p.second+1));
	kaku[p.second+1]=true;
      }
  }

  printf("%d\n",t);

  return 0;
}

