int n, l;
int a[100000];
long long int t[100000];
long long int ans;
typedef pair<int, int> P;
priority_queue<P, vector<P> > q;

int main(){
	scanf("%d %d", &n, &l);
	for(int i=1; i<n+1; i++){
		scanf("%d", &a[i]);
		q.push(P(a[i], i));
	}
	while(!q.empty()){
		P p = q.top(); q.pop();
		if(p.first > a[p.second-1] && p.first > a[p.second+1]) t[p.second] = l - a[p.second];
		else if((a[p.second-1] > a[p.second+1] && a[p.second+1] > p.first) || (a[p.second+1] > p.first && p.first > a[p.second-1])) t[p.second] = t[p.second+1] + l - p.first;
		else t[p.second] = t[p.second-1] + l - p.first;
		ans = max(ans, t[p.second]);
//		printf("%d %lld\n", p.first, t[p.second]);
	}
	printf("%lld\n", ans);
}

# include <iostream>
# include <algorithm>

# define MAX_N 10000

int n, l;
int a[MAX_N];

int memo[MAX_N];

int Icicle(int i) {
	int res = l - a[i];
	if (i == 0) {
		if (a[i] > a[i + 1])
			return memo[i] = res;
		else
			return memo[i] = res + Icicle(i + 1);
	}

	if (i == n - 1) {
		if (a[i] > a[i - 1])
			return memo[i] = res;
		else
			return memo[i] = res + Icicle(i - 1);
	}

	else {
		if (a[i - 1] < a[i] && a[i + 1] < a[i])
			return memo[i] = res;
		else if (a[i - 1] > a[i] && a[i + 1] < a[i])
			return memo[i] = res + Icicle(i - 1);
		else if (a[i - 1]<a[i] && a[i + 1]>a[i])
			return memo[i] = res + Icicle(i + 1);
		else
			return memo[i] = res + Icicle(i + 1) + Icicle(i - 1);
	}
}

int main() {
	std::cin >> n >> l;
	for (int i = 0; i < n; ++i)
		std::cin >> a[i];
	int res = 0;
	for (int i = 0; i < n; ++i) {
		res = std::max(res, Icicle(i));
	}
	std::cout << res << std::endl;
	return 0;
}

