int dfs(int x){
	if(ans[x]!=0)return ans[x];
	if(a[x]<a[x-1])ans[x]+=dfs(x-1);
	if(a[x]<a[x+1])ans[x]=max(ans[x],dfs(x+1));
	ans[x]+=l-a[x];
	return ans[x];
}

