bool S[100000]={false};
int L[100000]={0};
int main(void){
	long long int time=0;
	int n,l;
	cin >> n >>l;
	for(int i=0;i<n;i++){
		int tmp;cin >>tmp;L[i]=l-tmp;
	}
	int sum=0; 
	while(sum!=n){
		for(int i=0;i<n;i++){
			if(!S[i]){
				if(!i||L[i-1]==0){
					if(L[i]<L[i+1]||L[i+1]==0){
						S[i]=true;sum++;
					}
				}else if(i==n-1||L[i+1]==0){
					if(L[i]<L[i-1]||L[i-1]==0){
						S[i]=true;sum++;
					}
				}else{
					if((L[i]<L[i-1]||L[i-1]==0)&&(L[i]<L[i+1]||L[i+1]==0)){
						S[i]=true;sum++;
					}
				}
			}
		}
		int MIN=INF;
		for(int i=0;i<n;i++){
			if(S[i]&&L[i]>0)
				MIN=min(MIN,L[i]);
		}
		time+=MIN;
		for(int i=0;i<n;i++)
			if(S[i]&&L[i]>0)
				L[i]-=MIN;
	}
	int MAX=0;
	for(int i=0;i<n;i++)
		MAX=max(MAX,L[i]);
	time+=MAX;
	cout << time <<endl;
	return 0;
}

