      b[i]=m-a[i];
      for(j=i-1;j>0;j--){
	if(a[j-1]>a[j]){
	  b[j]=m-a[j]+(b[j-1]>b[j+1]?b[j-1]:b[j+1]);
	  break;
	}else
	  b[j]=m-a[j]+b[j+1];
      }
      for(j=i+1;j<n+1&&a[j-1]>a[j];j++)
	b[j]=m-a[j]+b[j-1];
    }else
      j=i;
  }
  for(i=1;i<n+1;i++){
    if(mx<b[i])
      mx=b[i];
  }
  cout<<mx<<endl;
  return 0;
}

