typedef pair<int,int> pii;

int memo[100010];
int n, l;

int solve(vector<int> &a, int idx){
  if(memo[idx] != 0) return memo[idx];

  if(idx == 0){
    if(a[idx] > a[idx+1]){
      memo[idx] = l - a[idx];
    }else{
      memo[idx] = l - a[idx] + solve(a, idx+1);
    }
  }else if(idx == n-1){
    if(a[idx] > a[idx-1]){
      memo[idx] = l - a[idx];
    }else{
      memo[idx] = l - a[idx] + solve(a, idx-1);
    }
  }else{
    memo[idx] = l - a[idx];
    if(a[idx] < a[idx+1]){
      memo[idx] = max(memo[idx], l - a[idx] + solve(a, idx+1));
    }
    if(a[idx]  < a[idx-1]){
      memo[idx] = max(memo[idx], l - a[idx] + solve(a, idx-1));
    }
  }
  return memo[idx];
}

int main(){

  cin >> n >> l;

  vector<int> a(n);
  REP(i,n){
    cin >> a[i];
  }

  REP(i,n){
    solve(a, i);
  }

  int ans = 0;
  REP(i,n){
