const int INF = 100000000;

int n, l;
int data[100000];
vector<int> nobi;

bool check(int i){
  if(i ==0){
    return data[i] > data[i+1];
  }else if(i == n-1){
    return data[i-1] < data[i];
  }else{
    return data[i-1] < data[i] && data[i] > data[i+1];
  }
}

main(){
  cin >> n >> l;
  for(int i=0;i<n;i++){
    cin >> data[i];
    if(data[i] >= l){
      data[i] = 0;
    }
  }
  int ans = 0;
  for(int i=0;i<n;i++) if(check(i)) nobi.push_back(i);
  for(int i=0;i<n;i++){
    int valm = INF;
    int pos;
    for(int i=0;i<nobi.size();i++){
      if(valm > l - data[nobi[i]]){
        valm = l - data[nobi[i]];
        pos = nobi[i];
      }
    }
    for(int i=0;i<nobi.size();i++){
      data[nobi[i]] += valm;
    }
    data[pos] = 0;
    vector<int>::iterator end_it = remove(nobi.begin(), nobi.end(), pos);
    nobi.erase(end_it, nobi.end());
    if(pos-1 >= 0) if(check(pos-1)) nobi.push_back(pos-1);
    if(pos+1 < n) if(check(pos+1)) nobi.push_back(pos+1);
    ans += valm;
  }
  cout << ans << endl;
  return 0;
}

