int n, L;
int a[100010];
int memo[100010];

int f(int x) {
  if (x == 0 || x == n + 1 || memo[x]) return memo[x];
  int res = L - a[x];
  if (a[x - 1] > a[x] && a[x] > a[x + 1]) {
    res += f(x - 1);
  } else if (a[x - 1] < a[x] && a[x] < a[x + 1]) {
    res += f(x + 1);
  } else if (a[x - 1] > a[x] && a[x] < a[x + 1]) {
    res += max(f(x - 1), f(x + 1));
  }
  return memo[x] = res;
}

int main(void) {
  cin >> n >> L;

  for (int i = 1; i <= n; i++) {
    cin >> a[i];
  }

  int ans = 0;
  for (int i = 1; i <= n; i++) {
    memo[i] = f(i);
    ans = max(ans, memo[i]);
  }

  cout << ans << endl;

  return 0;
}

