		if (nobiruka(i))pq.push(P(length[i], i));
	}
	int ans = 0;
	while (pq.size()) {
		auto tmp = pq.top(); pq.pop();

		ans = tmp.first;

		length[tmp.second] = 10000000;

		if (nobiruka(tmp.second + 1)) {
			pq.push(P(length[tmp.second + 1] + ans, tmp.second + 1));
		}

		if (nobiruka(tmp.second - 1)) {
			pq.push(P(length[tmp.second - 1] + ans, tmp.second - 1));
		}

	}

