int N, L;
int a[100002];

int dp[100002];

int rec(int i)
{
    if (~dp[i]) return dp[i];

    int cost = -1;

    if (a[i] < a[i - 1]) chmax(cost, rec(i - 1));
    if (a[i] < a[i + 1]) chmax(cost, rec(i + 1));

    int res;

    if (cost == -1) {
        res = L - a[i];
    } else {
        res = cost + (L - a[i]);
    }

    return dp[i] = res;
}

