#define p pair<int,int>
#define mp(a,b) make_pair(a,b)
using namespace std;

int main()
{
	int n, l;
	cin >> n >> l;
	vector<int> a(n);
	for (int i = 0; i < n; i++)cin >> a[i];
	vector<vector<int>> edge(n);
	vector<int> count(n);
	for (int i = 0; i < n - 1; i++)
	{
		if (a[i] > a[i + 1])
		{
			edge[i].push_back(i + 1);
			count[i + 1]++;
		}
	}
	for (int i = 1; i < n; i++)
	{
		if (a[i] > a[i - 1])
		{
			edge[i].push_back(i - 1);
			count[i - 1]++;
		}
	}
	queue<p> que; //where time
	for (int i = 0; i < n; i++)if (count[i] == 0)que.push(mp(i, 0));
	int ret = 0;
	while (!que.empty())
	{
		p now = que.front();
		int w = now.first;
		que.pop();
		count[w]--;
		for (auto x : edge[w])
		{
			count[x]--;
			if (count[x] == 0)que.push(mp(x,now.second + l - a[w]));
		}
		ret = max(ret, now.second + l - a[w]);
	}
	cout << ret << endl;
	return 0;
}

