# include <iostream>
# include <algorithm>

const int MAX_N = 100000;

int n, l;
int a[MAX_N];

int memo[MAX_N];

int Icicle(int i) {
	if (memo[i] > 0)
		return memo[i];

	int res = l - a[i];

	if (i == 0) {
		if (a[i] > a[i + 1])
			return memo[i] = res;
		else
			return memo[i] = res + Icicle(i + 1);
	}

	else if (i == (n - 1)) {
		if (a[i] > a[i - 1])
			return memo[i] = res;
		else
			return memo[i] = res + Icicle(i - 1);
	}

	else {
		if (a[i - 1] < a[i] && a[i + 1] < a[i])
			return memo[i] = res;
		else if (a[i - 1] > a[i] && a[i + 1] < a[i])
			return memo[i] = res + Icicle(i - 1);
		else if (a[i - 1]<a[i] && a[i + 1]>a[i])
			return memo[i] = res + Icicle(i + 1);
		else if (a[i - 1] > a[i] && a[i + 1]>a[i])
			return memo[i] = res + std::max(Icicle(i + 1),  Icicle(i - 1));
	}
}

int main() {
	std::cin >> n >> l;
	for (int i = 0; i < n; ++i)
		std::cin >> a[i];
	int res = 0;
	for (int i = 0; i < n; ++i) {
		res = std::max(res, Icicle(i));
	}
	std::cout << res << std::endl;
	return 0;
}

