	for(int i = 1; i <= n; i++){
		scanf("%d", &icicles[i]);
	}

	for(int i = 1; i <= n; i++){
		if(icicles[i] > icicles[i - 1] && icicles[i] > icicles[i + 1]){
			sum1 = sum2 = l - icicles[i];

			loop = i;
			while(icicles[loop] > icicles[--loop] && loop > 0){
				sum1 += l - icicles[loop];
			}
			if(icicles[loop] > icicles[i]){
				sum1 -= l - icicles[i];
			}
			ans = max(ans, sum1);

			loop = i;
			while(icicles[loop] > icicles[++loop] && loop <= n){
				sum2 += l - icicles[loop];
			}
			if(icicles[loop] > icicles[i]){
				sum2 -= l - icicles[i];
			}
			ans = max(ans, sum2);
		}
	}

	printf("%d\n", ans);
	return 0;
}

