typedef pair<int, int> P;

int N, L;
int length[100000];

bool nobiruka(int x) {
	if (x < 0 || x >= N)return false;
	if (x == 0 && length[0] < length[1])return true;
	else if (x == N - 1 && length[N - 1] < length[N - 2])return true;
	else if (length[x] < length[x + 1] && length[x] < length[x - 1])return true;
	return false;
}

int main() {
	cin >> N >> L;
	for (int i = 0; i < N; i++)
		cin >> length[i], length[i] = L - length[i];
	priority_queue<P, vector<P>, greater<P>> pq;
	for (int i = 0; i < N; i++) {
		if (nobiruka(i))pq.push(P(length[i], i));
	}
	int ans = 0;
	while (pq.size()) {
		auto tmp = pq.top(); pq.pop();

		//cout << tmp.first << "A" << tmp.second << endl;

		ans += (tmp.first - ans);
		cout << ans << endl;
		length[tmp.second] = 10000000;

		if (nobiruka(tmp.second + 1)) {
			pq.push(P(length[tmp.second + 1] + ans, tmp.second + 1));
		}

		if (nobiruka(tmp.second - 1)) {
			pq.push(P(length[tmp.second - 1] + ans, tmp.second - 1));
		}

	}

	cout << ans << endl;

	return 0;
}

