int N,L;
int len[100000];
int life[100000];
int dfs(int idx){
    if(life[idx]!=-1)return life[idx];
    int dif=L-len[idx];
    int res;
    if(idx==0)
        res=(len[idx]>len[idx+1])?dif:dif+dfs(idx+1);

    else if(idx==N-1)
        res=(len[idx]>len[idx-1])?dif:dif+dfs(idx-1);

    else if(len[idx]>len[idx+1]&&len[idx]>len[idx-1])
        res=dif;

    else if(len[idx]>len[idx+1])
        res=dif+dfs(idx-1);

    else if(len[idx]>len[idx-1])
        res=dif+dfs(idx+1);

    else res=dif+max(dfs(idx+1),dfs(idx-1));

    return life[idx]=res;
}
int main(){
    cin>>N>>L;
    for(int i=0;i<N;i++)cin>>len[i];
    fill(life,life+100000,-1);

    int Max=-1;
    for(int i=0;i<N;i++)
        Max=max(Max,dfs(i));

    cout<<Max<<endl;
}

