int N, L;
int dp[100000];

int f(int i, vector<int> &v) {
  if (dp[i]) return dp[i];
  int ma = 0;
  if (i - 1 >= 0 && v[i -1] > v[i]) ma = max(ma, f(i - 1, v));
  if (i + 1 < N && v[i + 1] > v[i]) ma = max(ma, f(i + 1, v));
  ma += L - v[i];
  return dp[i] = ma;
}

