typedef long long ll;
int a[100005];
int t[100005];
int N, L; 
int dfs(int x) {
  if(t[x] != -1) return t[x];
  if(x == 0) {
    if(a[x] > a[x+1]) {
      return t[x] = L - a[x];
    } else {
      return t[x] = L - a[x] + dfs(x+1);
    }
  } else if(x == N - 1) {
    if(a[x] > a[x-1]) {
      return t[x] = L - a[x];
    } else {
      return t[x] = L - a[x] + dfs(x-1);
    }
  } else {
    if(a[x] > a[x-1] && a[x] > a[x+1]) {
      return t[x] = L - a[x];
    } else if(a[x] > a[x-1]){
      return t[x] = L - a[x] + dfs(x+1);
    } else if(a[x] > a[x+1]) {
      return t[x] = L - a[x] + dfs(x-1);
    } else {
      return t[x] = L - a[x] + dfs(a[x-1] < a[x+1] ? x - 1 : x + 1);
    }
  }
}
