	priority_queue<P, vector<P>, greater<P>> pq;
	vector<P> buf;
	for (int i = 0; i < M; i++) {
		if (i == 0) {
			if (length[i + 1] > length[i])pq.push(P(length[i], i));
		}
		else if(i == M - 1) {
			if (length[i] < length[i - 1]) {
				pq.push(P(length[i], i));
			}
		}
		else {
			if (length[i] < length[i - 1] && length[i + 1] > length[i]) {
				pq.push(P(length[i], i));
			}
		}
	}
	int ans = 0;
	while (pq.size()) {
		auto tmp = pq.top(); pq.pop();
		//cout << tmp.first << " " << tmp.second << endl;
		ans += tmp.first;
		//cout << ans << endl;
		while (pq.size()) {
			buf.push_back(P(pq.top().first - tmp.first, pq.top().second));
			pq.pop();
		}
		for (int i = 0; i < buf.size(); i++)pq.push(buf[i]);
		length[tmp.second] = 0;
		buf.clear();

		if (tmp.second - 1 > 0) {
			if(length[tmp.second - 1] && length[tmp.second - 1] > length[tmp.second])
				pq.push(P(length[tmp.second - 1], tmp.second - 1));
		}

		if (tmp.second + 1 < M) {
			if (length[tmp.second + 1] && length[tmp.second + 1] > length[tmp.second])
				pq.push(P(length[tmp.second + 1], tmp.second + 1));
		}
	}
